import React, { useEffect, useState } from "react";
import SlideFirst from "./SlideFirst";
import SlideSecond from "./SlideSecond";
import SlideThird from "./SlideThird";

const ImageSlider = () => {
  const [slider, setSlider] = useState(0);
  const [allImages, setAllImages] = useState([
    SlideFirst(),
    SlideSecond(),
    SlideThird(),
  ]);

  useEffect(() => {
    setInterval(() => {
      setSlider((slider) => (slider < 2 ? slider + 1 : 0));
    }, 2000);
  }, []);

  return (
    <>
      <div>
        <div>{allImages[slider]}</div>
        <div>
          <button
            onClick={() => {
              if (slider > 0) {
                setSlider(slider - 1);
              }
            }}
          >
            Prev
          </button>
          <button
            onClick={() => {
              if (slider < allImages.length - 1) {
                setSlider(slider + 1);
              }
            }}
          >
            Next
          </button>
        </div>
      </div>
    </>
  );
};

export default ImageSlider;
