import React, { useEffect, useState } from "react";
import images1 from "../branding/images/1.jpg";
import images2 from "../branding/images/2.jpg";
import images3 from "../branding/images/3.jpg";
import images4 from "../branding/images/why.jpg";

const ImageSlider = () => {
  const [slider, setSlider] = useState(0);
  const [allImages, setAllImages] = useState([
    images1,
    images2,
    images3,
    images4,
  ]);

  useEffect(() => {
    setInterval(() => {
      setSlider((slider) => (slider < 2 ? slider + 1 : 0));
    }, 2000);
  }, []);

  return (
    <>
      <div>
        <img width={810} height={400} src={allImages[slider]} />
        <button
          onClick={() => {
            if (slider > 0) {
              setSlider(slider - 1);
            }
          }}
        >
          Prev
        </button>
        <button
          onClick={() => {
            if (slider < allImages.length - 1) {
              setSlider(slider + 1);
            }
          }}
        >
          Next
        </button>
      </div>
    </>
  );
};

export default ImageSlider;
